import axios, { AxiosRequestConfig } from 'axios';
import { getAuthData } from './storage';
import history from './history'


export const BASE_URL =
  process.env.REACT_APP_BACKEND_URL ?? 'http://142.171.135.63:8088/v1';

type LoginData = {
  login: string;
  password: string;
};

export const requestBackendLogin = (loginData: LoginData) => {

    return axios({
    method: 'POST',
    baseURL: BASE_URL,
    url: '/login',
    data: loginData,
  });
};

export const requestBackend = (config: AxiosRequestConfig) => {
  const headers = config.withCredentials
    ? {
        ...config.headers,
        Authorization: "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJBUEkgQVRNIiwic3ViIjoiYWRtaW4iLCJleHAiOjE3MDU1MzM5NTJ9.c2zSPEjwitNZbaqK_5K-I8UwtpgHoXpivKlY0Bc6czQ"
      }
    : config.headers;

  return axios({ ...config, baseURL: BASE_URL, headers });
};
axios.interceptors.request.use(
  function (config) {
    return config;
  },
  function (error) {
    return Promise.reject(error);
  }
);

axios.interceptors.response.use(
  function (response) {
    return response;
  },
  function (error) {
    if (error.response.status === 401) {
      history.push("/");
    }
    return Promise.reject(error);
  }
);
