import { useEffect, useState } from 'react';
import { Permission } from 'types/permission';
import { AxiosRequestConfig } from 'axios';
import { requestBackend } from 'util/requests';
import PermissionForm from './PermissionForm';
import PermissionTable from './PermissionTable';

import './styles.css';
import { userAcesskey } from 'util/users';

const PermissionView = () => {
  const [permissions, setPermissions] = useState<Permission[]>([]);

  useEffect(() => {
    const config: AxiosRequestConfig = {
      method: 'GET',
      url: '/permission',
      withCredentials: true,
    };
    requestBackend(config).then((response) => {
      setPermissions(response.data);
    });
  }, []);

  const handleInsertPermission = (permission: Permission) => {
    const clone = [...permissions];
    clone.push(permission);
    setPermissions(clone);
  };

  const handleDelete = (code: string) => {
    if (!window.confirm('Tem certeza que deseja deletar?')) {
      return;
    }

    const config: AxiosRequestConfig = {
      method: 'DELETE',
      url: `/permission/${userAcesskey}`,
      withCredentials: true,
    };

    requestBackend(config)
      .then(() => {
        // Atualiza a lista de permissões após a exclusão
        const updatedPermissions = permissions.filter(
          (permission) => permission.code !== code
        );
        setPermissions(updatedPermissions);
      })
      .catch((error) => {
        console.error('Erro ao excluir permissão:', error);
      });
  };

  return (
    <div className="permission-view-container">
      <PermissionForm onInsertPermission={handleInsertPermission} />
      <PermissionTable permission={permissions} onDelete={handleDelete} />
    </div>
  );
};

export default PermissionView;
