import { Permission } from 'types/permission';

import './styles.css';
import { userAcesskey } from 'util/users';
import { requestBackend } from 'util/requests';
import { AxiosRequestConfig } from 'axios';
import { useState } from 'react';

type Props = {
  permission: Permission[];
  onDelete : Function
};

const PermissionTable = ({ permission, onDelete }: Props) => {


  const handleDelete = (permission: Permission) => {
    if (!window.confirm('Tem certeza que deseja deletar ?')) {
      return;
    }

    const config: AxiosRequestConfig = {
      method: 'DELETE',
      url: `/permission/${userAcesskey}`,
      withCredentials: true,
    };

    requestBackend(config).then((response) => {
      onDelete()
    });
  };

  return (
    <div className="permission-table-container">
      <table className="table table-striped permission-table">
        <thead>
          <tr className="permission-table-header">
            <th>Código</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {permission.map((permission) => (
            <tr key={permission.code}>
              <td>{permission.code}</td>
              <td>{permission.description}</td>
              <td>
                <button className="btn btn-warning btn-sm">Editar</button>
                <button className="btn btn-danger btn-sm ml-2" onClick={() => handleDelete(permission)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PermissionTable;
